import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = ({handleClick, text}) => {
  return (
    <button onClick={handleClick}>{text}</button>
  )
}

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [total, setTotal] = useState(0);
  const [average, setAverage] = useState(0.0);
  const [positivePercentage, setPositivePercentage] = useState(0.0);

  const calculateNewStatistics = () => {
      setAverage((good-bad)/total);
      setPositivePercentage(good/total*100.0);
  }


  const handleGoodFeedback = () => {
    return () => {
      let newGood = good+1;
      let newTotal = total+1;
      setGood(newGood);
      setAverage((newGood-bad)/newTotal);
      setPositivePercentage(newGood/newTotal*100.0);
      setTotal(newTotal);
    }
  }

  const handleNeutralFeedback = () => {
    return () => {
      let newNeutral = neutral+1;
      let newTotal = total+1;
      setNeutral(newNeutral);
      setAverage((good-bad)/newTotal);
      setPositivePercentage(good/newTotal*100.0);
      setTotal(newTotal);
    }
  }

  const handleBadFeedback = () => {
    return () => {
      let newBad = bad+1;
      let newTotal = total+1;
      setBad(newBad);
      setAverage((good-newBad)/newTotal);
      setPositivePercentage(good/newTotal*100.0);
      setTotal(newTotal);
    }
  }

  return (
    <div>
    <h1>Give Feedback</h1>
    <Button handleClick={handleGoodFeedback()} text='good'/>
    <Button handleClick={handleNeutralFeedback()} text='neutral'/>
    <Button handleClick={handleBadFeedback()} text='bad'/>
    <h1>Statistics</h1>
    <p>good {good}</p>
    <p>neutral {neutral}</p>
    <p>bad {bad}</p>
    <p>all {total}</p>
    <p>average {average}</p>
    <p>positive {positivePercentage}%</p>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
