import ReactDOM from 'react-dom'

const Header = (props) => {
  return (
    <>
      <h1>{props.name}</h1>
    </>
  )
}

const Content = (props) => {
  return (
    <>
    <Part name={props.module.name} exercises={props.module.exercises}/>
    </>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.name} {props.exercises}
    </p>
  )
}

const Total = (props) => {
  return (
  <p>Number of exercises {props.total}</p>  
